import React, { useState } from "react";
import {
    View,
    Text,
    SafeAreaView,
    Image,
    StyleSheet,
    TouchableOpacity,
    Button,
    ScrollView,
    ActivityIndicator,
} from "react-native";
import * as ImagePicker from "expo-image-picker";
import { Feather } from "react-native-vector-icons";
import { createNativeStackNavigator } from "@react-navigation/native-stack";
import MapPage from "./MapPage";

const Stack = createNativeStackNavigator();

export default function Scan({
    locations,
    currentLoc,
    posts,
    setPosts,
    ownPosts,
    setOwnPosts,
}) {
    const [image, setImage] = useState(null);
    const [status, setStatus] = useState(null);
    const [types, setTypes] = useState(null);
    const API_KEY = "Enter your API key here";
    const API_URL = `https://vision.googleapis.com/v1/images:annotate?key=${API_KEY}`;

    async function callGoogleVisionAsync(image, uri) {
        const body = {
            requests: [
                {
                    image: {
                        content: image,
                    },
                    features: [
                        {
                            type: "LABEL_DETECTION",
                            maxResults: 3,
                        },
                    ],
                },
            ],
        };

        const response = await fetch(API_URL, {
            method: "POST",
            headers: {
                Accept: "application/json",
                "Content-Type": "application/json",
            },
            body: JSON.stringify(body),
        });
        const result = await response.json();

        const results = [];
        result.responses[0].labelAnnotations.map((obj) => {
            results.push({ kind: obj.description, accuracy: obj.score });
        });
        console.log("callGoogleVisionAsync -> result", results);
        setTypes(results);
        setPosts([
            {
                user: "Olivia",
                userInfo: "CS Student at Queen's",
                pfp: "/Users/oliviachenxu/Documents/QueensGDSC/App/assets/olivia.jpg",
                image: uri,
                description: "üéâ Earned 40 points for scanning an item üéâ",
                createdAt: new Date(),
            },
            ...posts,
        ]);
        setOwnPosts([
            {
                user: "Olivia",
                userInfo: "CS Student at Queen's",
                pfp: "/Users/oliviachenxu/Documents/QueensGDSC/App/assets/olivia.jpg",
                image: uri,
                description: "üéâ Earned 40 points for scanning an item üéâ",
                createdAt: new Date(),
            },
            ...ownPosts,
        ]);
    }

    const pickImage = async () => {
        // No permissions request is necessary for launching the image library
        const { cancelled, uri, base64 } =
            await ImagePicker.launchImageLibraryAsync({
                base64: true,
            });

        if (!cancelled) {
            setImage(uri);
            try {
                const result = await callGoogleVisionAsync(base64, uri);
                setStatus(result);
            } catch (error) {
                setStatus(`Error: ${error.message}`);
            }
        } else {
            setImage(null);
            setStatus(null);
        }
    };
    const openCamera = async () => {
        // Ask the user for the permission to access the camera
        const permissionResult =
            await ImagePicker.requestCameraPermissionsAsync();

        if (permissionResult.granted === false) {
            alert("You've refused to allow this appp to access your camera!");
            return;
        }

        const { cancelled, uri, base64 } = await ImagePicker.launchCameraAsync({
            base64: true,
        });

        if (!cancelled) {
            setImage(uri);
            setStatus("Loading...");
            try {
                const result = await callGoogleVisionAsync(base64, uri);
                setStatus(result);
            } catch (error) {
                setStatus(`Error: ${error.message}`);
            }
        } else {
            setImage(null);
            setStatus(null);
        }
    };
    const Main = ({ navigation }) => {
        return (
            <SafeAreaView>
                {image ? (
                    <ScrollView>
                        <View
                            style={{ backgroundColor: "#5DB075", height: 200 }}
                        >
                            <TouchableOpacity
                                style={{
                                    flexDirection: "row",
                                    alignItems: "center",
                                    marginTop: 10,
                                }}
                                onPress={() => {
                                    setTypes(null);
                                    setImage(null);
                                }}
                            >
                                <Feather
                                    name="chevrons-left"
                                    size={30}
                                    color="white"
                                ></Feather>
                                <Text
                                    style={{
                                        color: "white",
                                        fontWeight: "600",
                                        fontSize: 16,
                                    }}
                                >
                                    Back
                                </Text>
                            </TouchableOpacity>
                            <Text
                                style={{
                                    alignSelf: "center",
                                    color: "white",
                                    fontSize: 30,
                                    fontWeight: "700",
                                }}
                            >
                                Scan Results üîé
                            </Text>
                        </View>
                        <View
                            style={{
                                position: "absolute",
                                alignSelf: "center",
                                top: 100,
                                shadowColor: "black",
                                shadowOpacity: 0.2,
                                shadowRadius: 10,
                            }}
                        >
                            <Image
                                source={{ uri: image }}
                                style={{
                                    width: 160,
                                    height: 160,
                                    borderRadius: 80,
                                }}
                            />
                        </View>

                        <View
                            style={{
                                alignItems: "center",
                                paddingVertical: 80,
                            }}
                        >
                            <Text
                                style={{
                                    fontSize: 28,
                                    fontWeight: "700",
                                }}
                            >
                                You have scanned:
                            </Text>
                            {!types && (
                                <ActivityIndicator
                                    size="large"
                                    color="#4B9460"
                                    marginTop={20}
                                />
                            )}
                            {types &&
                                types.map((type) => (
                                    <View
                                        style={{
                                            flexDirection: "column",
                                            alignItems: "center",
                                            marginTop: 10,
                                        }}
                                    >
                                        <Text
                                            style={{
                                                fontSize: 20,
                                                fontWeight: "600",
                                                opacity: 0.6,
                                            }}
                                        >
                                            {type.kind}
                                        </Text>
                                        <Text
                                            style={{
                                                opacity: 0.4,
                                            }}
                                        >{`(${(type.accuracy * 100).toFixed(
                                            2
                                        )}% likely)`}</Text>
                                    </View>
                                ))}
                            {types && (
                                <>
                                    <Text
                                        style={{
                                            fontSize: 24,
                                            fontWeight: "700",
                                            marginTop: 20,
                                        }}
                                    >
                                        üéâ 40 Points Earned! üéâ
                                    </Text>
                                    <View style={styles.card}>
                                        <Text
                                            style={{
                                                color: "#4B9460",
                                                fontSize: 20,
                                                fontWeight: "700",
                                            }}
                                        >
                                            Recycling Categories:
                                        </Text>
                                        <View
                                            style={{
                                                flexDirection: "row",
                                                marginVertical: 10,
                                                justifyContent: "center",
                                            }}
                                        >
                                            <View style={styles.tag}>
                                                <Text style={styles.kind}>
                                                    Can
                                                </Text>
                                            </View>
                                            <View style={styles.tag}>
                                                <Text style={styles.kind}>
                                                    Scrap Metal
                                                </Text>
                                            </View>
                                        </View>
                                        <TouchableOpacity
                                            style={styles.add}
                                            onPress={() =>
                                                navigation.navigate("MapPage")
                                            }
                                        >
                                            <Text
                                                style={{
                                                    color: "white",
                                                    fontWeight: "700",
                                                    fontSize: 16,
                                                    textAlign: "center",
                                                    marginRight: 10,
                                                }}
                                            >
                                                üìçFind Nearest Recyling Bin
                                            </Text>
                                            <Feather
                                                name="chevrons-right"
                                                size={30}
                                                color="white"
                                            ></Feather>
                                        </TouchableOpacity>
                                    </View>
                                    <View style={styles.fact}>
                                        <Text
                                            style={{
                                                color: "#4B9460",
                                                fontSize: 20,
                                                fontWeight: "700",
                                            }}
                                        >
                                            ü§îDid you know?
                                        </Text>
                                        <Text style={styles.fact_text}>
                                            Companies can produce as many as
                                            80,000,000 Aluminum cans every year?{" "}
                                        </Text>
                                        <Text style={styles.fact_text}>
                                            That‚Äôs a lot of soda!
                                        </Text>
                                    </View>
                                </>
                            )}
                        </View>
                    </ScrollView>
                ) : (
                    <View
                        style={{
                            backgroundColor: "black",
                            justifyContent: "center",
                            height: "100%",
                        }}
                    >
                        <View
                            style={{
                                backgroundColor: "white",
                                alignSelf: "center",
                                borderRadius: 10,
                                shadowColor: "black",
                                shadowOpacity: 0.2,
                                shadowRadius: 50,
                                paddingHorizontal: 10,
                                paddingVertical: 10,
                            }}
                        >
                            <Button
                                onPress={openCamera}
                                title="Open camera"
                                color={"grey"}
                            />
                            <Button
                                title="Pick an image from camera roll"
                                onPress={pickImage}
                                color={"grey"}
                            />
                        </View>
                    </View>
                )}
            </SafeAreaView>
        );
    };

    return (
        <Stack.Navigator screenOptions={{ headerShown: false }}>
            <Stack.Screen name="Main" component={Main}></Stack.Screen>
